import axios from 'axios';
import config from './config'

// axios.defaults.BaseURL = config.apiUrl;

export const getLoginToken = obj => {
  return axios({
    method: 'post',
    url: config.apiUrl + '/login',
    headers: { 'content-type': 'application/x-www-form-urlencoded' },
    data: obj
  });
}
