import React from 'react';
import './Banner.css';
import { pages } from './App.js';
import { postLogout } from './api.js';

// A banner component
class Banner extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      user: props.user,
      appCallback: props.callback,
    };
    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  // handle click "go to login page" button
  handleLogin(e){
    this.state.appCallback({
      page: pages.login,
      user: null
    });
  }

  // handle logout
  // If everything OK then change the page of the App and remove user
  // else alert user
  handleLogout(e){
    if(this.user !== null){
      let reqObj = this.user.token;
      postLogout(reqObj)
        .then(
          json => {
            setTimeout(() => {
              this.state.appCallback({
                page: page.main,
                user: null
              })
            }, 0)
          })
          .catch(err => {
            alert("Got a problem while trying to logout user " + this.state.user.username + ":\n" + err.message);
          });
    }
    postLoginToken(reqObj)
      .then(json => {
        setTimeout(() => {
          this.setState({ token: json.data.token });
          let finalUser = {
            username: this.state.username,
            password: this.state.password, // maybe this is not a good IDEA
            token: this.state.token
          };
          this.state.appCallback({
            page: pages.main,
            user: finalUser
          });
        }, 0)
      })
      .catch(err => {
        this.setState({ error: err.message });
      });
  }

  render(){
    let userName = this.state.user ? this.state.user.username : "<anonymous>";
    return(
      <div className="banner">
        <h4>EvCharge</h4>
        {this.state.user !== null &&(
          <div className="dropdown">
            <button
              type="button"
              className="user_btn"
              name="user_btn"
              onClick={this.handleLogout}
            >
              {userName}
            </button>
            <p>Logout</p>
          </div>
        )}
        {this.state.user === null &&(
          <button
            type="button"
            className="login_btn"
            name="login"
            onClick={this.handleLogin}
          >
            Login
          </button>
        )}
      </div>
    );
  }
}

export default Banner;
